#include <stdio.h>#include <stdlib.h>#include <math.h>int main (int argc, char **argv) {    float val, low, high, mid, oldmid, midsqr;    val = atof (argv[1]);    if (val < 0) {       printf ("Invalid input.");       return 1;    }    // Set initial bounds and print heading.    low = 0.0;    high = 16.0;    mid = 0.0;    oldmid = 1.0;    // Keep going until accurate enough.    while (fabs(oldmid - mid)>= 0.00001) {        oldmid = mid;        // Get midpoint and see if we need lower or higher.        mid = (high + low) / 2;        midsqr = mid * mid;        if (midsqr> val) {            high = mid;            printf ("- too high\n");        } else {            low = mid;            printf ("- too low\n");        }    }    // Desired accuracy reached, print it.    printf ("sqrt(%.4f) = %.4f\n", val, mid);    return 0;}